import React from 'react'

export default function Question(props) {
    console.log(props)
    
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)

    const answersArray = props.incorrect.map(answer => 
        answer
    )
    answersArray.push(props.correct)
    
    const answ = answersArray.map(answer => { <p className='que-answer'>${answer}</p>})

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    {answ}
                </div>
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
