import React from 'react'

export default function Question(props) {
    console.log(props)
    
    // const answersArray = props.incorrect.map(answer => 
    //     `<p className='que-answer'>${answer}</p>`
    // )
    // answersArray.push(`<p className='que-answer'>${props.correct}</p>`)
    // console.log(answersArray)
    
    function create(){
        return (`
                <div className='que-face'>
                    <h2 className='que-question'>${props.question}</h2>
                    <div className='que-answers'>
                        <p className='que-answer'>${props.correct}</p>
                        <p className='que-answer'>${props.incorrect}</p>
                        <p className='que-answer'>${props.incorrect}</p>
                        <p className='que-answer'>${props.incorrect}</p>
                    </div>
                    <button 
                        className='que-button'
                    >
                        Check answers
                    </button>
                </div>
            `)
        }
    return (
        <>
            {create()}
        </>
    )
}
