import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const answersArray = function shuffle() {
            const newArr = [...props.answers]
            let currentIndex = newArr.length,  randomIndex
          
            while (currentIndex !== 0) {
              randomIndex = Math.floor(Math.random() * currentIndex)
              currentIndex--
          
              [newArr[currentIndex], newArr[randomIndex]] 
              = [newArr[randomIndex], newArr[currentIndex]]
            }
            return newArr
    }

    const testArr = answersArray().map(
        answer => {return ({...answer, s: false})} 
    )
        console.log(testArr())
    const createAnswers = answersArray().map(
        (ans, key) => {
            return (
                    <button
                    className={'que-answer'} 
                    key={key} 
                    onClick={ 
                        e => props.checkCorrect(
                                e.target.innerText, 
                                props.correct, 
                                e.target, 
                                props.id, 
                                props.answers
                            )
                    }
                >
                    {ans}
                </button>
            )
        }
    )

