import React from 'react'

export default function Question(props) {
    const styles = {
        background: props.disabled ? '#D6DBF5' : '#F5F7FB',
        border: props.disabled ? 'none' : '1px solid #293264'
    }
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const question = {
        question: props.question,
        correct: props.correct,
        incorrect: props.incorrect
    }

    function CreateAnswers() {
        const answers = question.incorrect
        answers.push(question.correct)
        console.log(answers)
        return answers.map( (ans, key) => 
            <button
                style={styles}
                disabled={props.disabled}
                className='que-answer' 
                key={key} 
                onClick={ e => props.checkCorrect(e.target.innerText, question.correct, e.target, props.id, props.disabled, answers)}
            >
                {ans}
            </button>
        )
    }

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{question.question}</h2>
                <div className='que-answers'>
                    <CreateAnswers />
                </div>
            </div>
        </>
    )
}
