import React from 'react'

export default function Question(props) {
    console.log(props)

    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const question = {
        question: props.question,
        correct: props.correct,
        incorrect: props.incorrect
    }
    const flag = true
    const flagg = flag ? 'disabled' : ''
    function CreateAnswers() {
        const incorrectArr = question.incorrect
        incorrectArr.push(question.correct)

        return incorrectArr.map( (ans, key) => 
            <button 
                disabled={flagg}
                className='que-answer' 
                key={key} 
                onClick={ e => props.checkCorrect(e.target.innerText, question.correct, e.target)}
            >
                {ans}
            </button>
        )
    }

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{question.question}</h2>
                <div className='que-answers'>
                    <CreateAnswers />
                </div>
            </div>
        </>
    )
}
