        ans.selected 
                                ? 'que-answer active'
                                : ans.correct ? 'que-answer correct'
                                : 'que-answer'
    }

    const createAnswers = props.answers.map(
        (ans, key) => {
            return (
                    <button
                    className={sa
                    } 
                    key={key} 
                    disabled={props.disabled}
                    onClick={ 
                        e => props.checkSelected(
                            ans,
                            ans.id,
                            props.id,
                            ans.correct
                        )
                    }
                >
                    {ans.answer}
                </button>
            )
        }
    )

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    {createAnswers}
                </div>
            </div>
        </>
    )
}
