import React from 'react'
import Question from './Question'

export default function Quiz() {
    const [questions, setQuestions] = React.useState([])
    const [disabled, setDisabled] = React.useState(false)
    
    React.useEffect( () => {
        async function getQuestions() {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()
            console.log(data)
            setQuestions(data.results)
        }
        getQuestions()
    }, [])
    
    function checkCorrect(answer, correct, target, id) {
        target.classList.toggle('active')
        setQuestions(oldQuestions => oldQuestions.map( question => {
            return id === question.id
                ?   {
                        disabled: true,
                        ...question
                    }
                :   {...question}
        }))
        // if(que.selected) {
        //     que.selected && target.classList.add('active')

        // }
        
        
        // console.log(answer)
        // console.log(correct)
        // console.log(que.selected)
    }


    const allQuestions = questions.map((question, key) => {    
        return (
            <Question
                key={key}
                id={key}
                disabled={disabled}
                question={question.question}
                correct={question.correct_answer}
                incorrect={question.incorrect_answers}
                checkCorrect={checkCorrect}
            />
        )
    })

    return (
        <>  
            <div className='que-container'>
                {allQuestions}
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
