import React from 'react'

export default function Question(props) {
    console.log(props)
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const [correct, setCorrect] = React.useState(answers.correct)
    const answers = {
        correct: props.correct,
        incorrect: props.incorrect
    }
    setCorrect()
    function InAn() {
        const incorrect = answers.incorrect
        return answers.incorrect.map( (ans, key) => <p className='que-answer' key={key}>{ans}</p>)
    }
    
    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    
                    <InAn />
                </div>
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
