import React from 'react'
import Question from './Question'
import { nanoid } from 'nanoid'

export default function Quiz(props) {
    React.useEffect( () => {
        const [questions, setQuestions] = React.useState([])
    const [buttonDisabled, setButtonDisabled] = React.useState(true)
    const [allSelected, setAllSelected] = React.useState(false)
    React.useEffect( () => {
        async function getQuestions() {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()
        
            setQuestions(data.results.map((question, id) => ({
                id: id,
                question: question.question,
                correct: question.correct_answer,
                disabled: false,
                answers: [question.correct_answer, ...question.incorrect_answers]
            })))

            //shuffling an answers
            setQuestions(oldQuestions => oldQuestions.map(question => {
                const answersArr = shuffle(question.answers)
                return ({
                    ...question,
                    answers: answersArr.map(answer => ({
                        id: nanoid(),
                        answer: answer,
                        selected: false,
                        correct: question.correct === answer
                                ? true : false   
                    }))     
                })
            }
            ))
            
            function shuffle(shuffleArr) {
                let currentIndex = shuffleArr.length,  randomIndex
              
                while (currentIndex !== 0) {
                  randomIndex = Math.floor(Math.random() * currentIndex)
                  currentIndex--
              
                  [shuffleArr[currentIndex], shuffleArr[randomIndex]] 
                  = [shuffleArr[randomIndex], shuffleArr[currentIndex]]
                }
                return shuffleArr
            }

        }
        getQuestions()
    }, [])  

    function checkSelected(ans, id, targetId, correct) {
        setQuestions(oldQuestions => oldQuestions.map(question => ({
            ...question,
            disabled: question.id === targetId 
                        ? !question.disabled 
                        : question.disabled,
            answers: question.answers.map(answer => {
                return answer.id === id
                    ? {...answer, selected: !answer.selected}
                    : answer
            })
        })))
    }

    const allQuestions = questions.map( 
        ( {id, question, correct, answers, disabled} , key) => {
            return (
                <Question
                    key={key}
                    id={id}
                    disabled={disabled}
                    question={question}
                    allSelected={allSelected}
                    correct={correct}
                    answers={answers}
                    checkSelected={checkSelected}
                />
            )
    })

    React.useEffect( () => {
        if(questions.length > 0) {
            const allSelected = questions.every(
                question => question.disabled
            )
    
            if(allSelected) setButtonDisabled(false)
            
        }

    }, [questions])

    function checkAnswers(e) {
        e.preventDefault()
        setAllSelected(true)
        setButtonDisabled(true)
    }
    }, [])

    return (
        <>  
            <form className='que-container'>
                {allQuestions}
                <button 
                    className='que-button'
                    disabled={buttonDisabled}
                    onClick={checkAnswers}
                >
                    Check answers
                </button>
                <button 
                    className='home-button'
                    onClick={ () => props.newGame}
                >
                    New game
                </button>
            </form>
        </>
    )
}
