import React from 'react'
import Question from './Question'

export default function Quiz() {
    const [questions, setQuestions] = React.useState([])

    React.useEffect( () => {
        async function getQuestions() {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()
        
            setQuestions(data.results.map((question, id) => ({
                id: id,
                question: question.question,
                correct: question.correct_answer,
                answers: [question.correct_answer, ...question.incorrect_answers]
            })))

            setQuestions(oldQuestions => oldQuestions.map(question => ({
                        ...question,
                        answers: shuffle(question.answers)       
                    })
            ))
            
            function shuffle(shuffleArr) {
                let currentIndex = shuffleArr.length,  randomIndex
              
                while (currentIndex !== 0) {
                  randomIndex = Math.floor(Math.random() * currentIndex)
                  currentIndex--
              
                  [shuffleArr[currentIndex], shuffleArr[randomIndex]] 
                  = [shuffleArr[randomIndex], shuffleArr[currentIndex]]
                }
                return shuffleArr
            }

            setQuestions(oldQuestions => oldQuestions.map(question => {
                const answersArr = []
                question.answers.map(answer => answersArr.push({
                    answer: answer,
                    selected: false      
            }))
                return ({
                    ...question,
                    answers: answersArr
                })
            }))

        }
        getQuestions()
    }, [])  

    console.log(questions)

    function checkCorrect(answer, correct, target, id, answers) {
        console.log(id)
    }

    const allQuestions = questions.map( 
        ( {id, question, correct, answers} , key) => {
            return (
                <Question
                    key={key}
                    id={id}
                    question={question}
                    correct={correct}
                    answers={answers}
                    checkCorrect={checkCorrect}
                />
            )
    })

    return (
        <>  
            <div className='que-container'>
                {allQuestions}
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
