import React from 'react'
import { useId } from "react";

export default function Questions() {
    const [questions, setQuestions] = React.useState([])

    React.useEffect( () => {
        async function getQuestions() {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()

            setQuestions(data.results)
        }
        getQuestions()
       
    }, [])

    const qArr = questions.map(question => {
                return `
                        <h2 className='que-question'>${question.question}</h2>
                        <div className='que-answers'>
                            <p className='que-answer'>${question.incorrect_answers[0]}</p>
                            <p className='que-answer'>${question.incorrect_answers[1]}</p>
                            <p className='que-answer'>${question.incorrect_answers[2]}</p>
                            <p className='que-answer'>Answer 4</p>
                        </div>
                    `
                
            }
        )
        console.log(qArr)
    
    return (
        <>
            <div className='que-face'>
                {qArr}
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
