import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
console.log(props)

    function CreateAnswers() {
        const answersArr = []
        props.answers.map( (answer) => 
            answersArr.push(answer)
        )
        console.log(answersArr)
        for(let answer of answersArr) {
            return (
                <p
                    className={'que-answer'} 
                    key={key} 
                    onClick={ 
                        e => props.checkCorrect(
                                e.target.innerText, 
                                props.correct, 
                                e.target, 
                                props.id, 
                                props.answers
                            )
                    }
                >
                    {answersArr[answer]}
                </p>
            )
        }
    }

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    <CreateAnswers />
                </div>
            </div>
        </>
    )
}
