import React from 'react'

export default function Question(props) {
    console.log(props)
    
    function showAnswers() {
        const answersArray = []
        
        props.incorrect.map(answer => {
            answersArray.push(`<p className='que-answer'>${answer}</p>`)
        })
        return answersArray
    }
    
    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    <p className='que-answer'>{props.correct}</p>
                    {showAnswers().join('')}
                </div>
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
