import React from 'react'
import Home from './Home'
import Quiz from './Quiz'

export default function App() {
  const app = express()
  app.use("/src", express.static('./src/'));

  const [start, setStart] = React.useState(false)

  function newGame() {
    setStart(() => !start)
  }

  return (
    <>
      <React.StrictMode>
      {start ? <Quiz /> : <Home newGame={newGame} />}
      </React.StrictMode>
    </>
  )
}
