import React from 'react'
import Question from './Question'

export default function Quiz() {
    const [questions, setQuestions] = React.useState([])
    const [questionsArray, setQuestionsArray] = React.useState([])

    React.useEffect( () => {
        async () => {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()
            
            setQuestions(data.results.map((question, id) => ({
                id: id,
                question: question.question,
                correct: question.correct_answer,
                incorrect: question.incorrect_answers,
                disabled: false
            })))
        }
    }, [])





    function checkCorrect(answer, correct, target, id, answers) {
        console.log(answers)
        setQuestions(oldQuestions => oldQuestions.map( question => {
            console.log(question)
            return question.id === id
            ?   {
                ...question,
                disabled: !question.disabled
            }
            :   question
        }))
            console.log(questions)
        // if(que.selected) {
        //     que.selected && target.classList.add('active')

        // }
        
        
        // console.log(answer)
        // console.log(correct)
        // console.log(que.selected)
    }


    const allQuestions = questions.map( (question, key) => { 
        return (
            <Question
                key={key}
                id={question.id}
                disabled={question.disabled}
                question={question.question}
                correct={question.correct}
                incorrect={question.incorrect}
                checkCorrect={checkCorrect}
            />
        )
    })

    return (
        <>  
            <div className='que-container'>
                {allQuestions}
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
