import React from 'react'

export default function Question(props) {
    console.log(props)
    
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const test = {
        correct: props.correct,
        incorrect: props.incorrect
    }
    console.log(test)
    
    const incorrectAnswers = test.incorrect.map(answer =>
            <p className='que-answer'>${answer}</p>
    ).join('')

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    <p className='que-answer'>{test.correct}</p>
                    {incorrectAnswers}
                </div>
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
