import React from 'react'
import Home from './Home'
import Quiz from './Quiz'

export default function App() {
  const [start, setStart] = React.useState(false)

  function newGame() {
    setStart(() => !start)
  }

  return (
    <>
      {start ? <Quiz /> : <Home newGame={newGame} />}
    </>
  )
}
