import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    function shuffleAnswers(array) {
            let currentIndex = array.length,  randomIndex;
          
            // While there remain elements to shuffle...
            while (currentIndex !== 0) {
          
              // Pick a remaining element...
              randomIndex = Math.floor(Math.random() * currentIndex);
              currentIndex--;
          
              // And swap it with the current element.
              [array[currentIndex], array[randomIndex]] = [
                array[randomIndex], array[currentIndex]];
            }
            return array
        }
          
          console.log(shuffleAnswers(props.answers));
        

    const createAnswers = props.answers.map( (ans, key) => {
            return (
                        <p
                        className={props.selected 
                                    ? 'que-answer active'
                                    : 'que-answer'
                        } 
                        key={key} 
                        onClick={ 
                            e => props.checkCorrect(
                                    e.target.innerText, 
                                    props.correct, 
                                    e.target, 
                                    props.id, 
                                    props.answers
                                )
                        }
                    >
                        {ans}
                    </p>
                )
            }
        )

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    {createAnswers}
                </div>
            </div>
        </>
    )
}
