import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    function className(answer) {
        if(props.allSelected && answer.correct) 'que-answer correct'
        else if (answer.selected) 'que-answer active' 
        else 'que-answer'
    }

    const createAnswers = props.answers.map(
        (answer, key) => {
            return (
                    <button
                    className={className(answer)} 
                    key={key} 
                    disabled={props.disabled}
                    onClick={ 
                        e => props.checkSelected(
                            answer,
                            answer.id,
                            props.id
                        )
                    }
                >
                    {answer.answer}
                </button>
            )
        }
    )

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    {createAnswers}
                </div>
            </div>
        </>
    )
}
