import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    function createsAnswers() {
        console.log(props.answers.length)
        console.log(props.question)
        for(let i = 0; i < props.answers.length; i++) {
            const randomAnswer = Math.ceil(Math.random() * props.answers.length)
            console.log(props.answers[randomAnswer])
        }
    }
    createsAnswers()

    const createAnswers = props.answers.map( (ans, key) => {
            return (
                        <p
                        className={props.selected 
                                    ? 'que-answer active'
                                    : 'que-answer'
                        } 
                        key={key} 
                        onClick={ 
                            e => props.checkCorrect(
                                    e.target.innerText, 
                                    props.correct, 
                                    e.target, 
                                    props.id, 
                                    props.answers
                                )
                        }
                    >
                        {ans}
                    </p>
                )
            }
        )

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    {createAnswers}
                </div>
            </div>
        </>
    )
}
