import React from 'react'
import { decodeEntity } from 'html-entities';

export default function Question(props) {

    function className(answer) {
            if(
                props.allSelected 
                && !answer.correct 
                && answer.selected
            ) 
            return 'que-answer incorrect'
            else if (
                props.allSelected 
                && answer.correct
            ) 
            return 'que-answer correct'
            else if (
                answer.selected
            ) 
            return 'que-answer active'
            else return 'que-answer'
    }
    console.log(decodeEntity(props.question))
    const createAnswers = props.answers.map(
        (answer, key) => {
            return (
                    <button
                    className={className(answer)} 
                    key={key} 
                    disabled={props.disabled}
                    onClick={ () => props.checkSelected(
                            answer,
                            answer.id,
                            props.id
                        )
                    }
                >
                    {answer.answer}
                </button>
            )
        }
    )

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{decodeEntity(props.question, {level: 'html5'})}</h2>
                <div className='que-answers'>
                    {createAnswers}
                </div>
            </div>
        </>
    )
}
