import React from 'react'
import Question from './Question'
import { nanoid } from 'nanoid'

export default function Quiz() {
    const [questions, setQuestions] = React.useState([])

    React.useEffect( () => {
        async function getQuestions() {
            const res = await fetch('https://opentdb.com/api.php?amount=10')
            const data = await res.json()
        
            setQuestions(data.results.map((question, id) => ({
                id: id,
                question: question.question,
                correct: question.correct_answer,
                selected: false,
                answers: [question.correct_answer, ...question.incorrect_answers]
                // .map(answer => ({answer, id:nanoid()}))
            })))
            setQuestions(oldQuestions => oldQuestions.map(
                question => ({
                    ...question,
                    [question.answers]: question.answers.map(
                        answer => {return ({...answer, selected: false})}
                    )
                })
            ))
        }
        getQuestions()
    }, [])
    

    console.log(questions)
    function checkCorrect(answer, correct, target, id, answers) {
        console.log(id)
        
        console.log(questions[id].answers)
        
        
        // setQuestions(oldQuestions => oldQuestions.map(
        //     question => {
        //         console.log(question)
        //         return question.id === id
        //             ?   {...question, selected: !question.selected}
        //             :   question
        //     }
        // ))
    }


    const allQuestions = questions.map( 
        ( {id, disabled, question, correct, answers, selected} , key) => {
            return (
                <Question
                    key={key}
                    id={id}
                    selected={selected}
                    question={question}
                    correct={correct}
                    answers={answers}
                    checkCorrect={checkCorrect}
                />
            )
    })

    return (
        <>  
            <div className='que-container'>
                {allQuestions}
                <button 
                    className='que-button'
                >
                    Check answers
                </button>
            </div>
        </>
    )
}
