    const createAnswers = props.answers.answer.map(
        (ans, key) => {
            return (
                    <button
                    className={'que-answer'} 
                    key={key} 
                    onClick={ 
                        e => props.checkCorrect(
                                e.target.innerText, 
                                props.correct, 
                                e.target, 
                                props.id, 
                                props.answers
                            )
                    }
                >
                    {ans}
                </button>
            )
        }
    )

