import React from 'react'

export default function Question(props) {
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
console.log(props)
    const AnswersArray = []

    function CreateAnswers() {
        props.answers.map( (ans, key) => 
            AnswersArray.push(
                (<p
                className={props.selected 
                    ? 'que-answer active' 
                    : 'que-answer'
                } 
                key={key} 
                onClick={ 
                    e => props.checkCorrect(
                            e.target.innerText, 
                            props.correct, 
                            e.target, 
                            props.id, 
                            props.answers
                        )
                }
                >
                    {ans}
                </p>)
            )
        )
    }CreateAnswers()

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    <AnswersArray />
                </div>
            </div>
        </>
    )
}
