import React from 'react'

export default function Question(props) {
    console.log(props)
    const [selected, setSelected] = React.useState(false)
    // const question = props.question.replace(/&quot;/g, '"').replace(/&#039;/g, `'`)
    const answers = {
        correct: props.correct,
        incorrect: props.incorrect
    }

    function CreateAnswers() {
        const incorrectArr = answers.incorrect
        incorrectArr.push(answers.correct)
        return incorrectArr.map( (ans, key) => 
            <p 
                className='que-answer' 
                key={key} 
                onClick={ e => {
                    props.checkCorrect(e.target.innerText, answers.correct, e.target)
                }}
            >
                {ans}
            </p>
        )
    }

    return (
        <>
            <div className='que-face'>
                <h2 className='que-question'>{props.question}</h2>
                <div className='que-answers'>
                    <CreateAnswers />
                </div>
            </div>
        </>
    )
}
